#include <stdio.h>

int main()
{   
    // this is a type declaration instruction which tells the compiler to create a variable named i of type int
    int i = 10;
    int j = i;
    int a = 2, b = 3, c = 4, d = 5;

    printf("the value of i is %d and the value of j is %d", i, j);
    printf("\nthe value of a is %d and the value of b is %d", a, b);
    printf("\nthe value of c is %d and the value of d is %d", c, d);
    return 0;
} 